const zenroom = global.get("zenroom");

const encoded_variables = JSON.stringify(msg.variables)

const sign_script = `
Scenario eddsa: sign a graph query
Given I have a 'base64' named 'gql'
Given I have a 'keyring'

# Fix Apollo's mingling with query string
When I remove spaces in 'gql'
and I compact ascii strings in 'gql'

When I create the eddsa signature of 'gql'
And I create the hash of 'gql'

Then print 'eddsa signature' as 'base64'
Then print 'gql' as 'base64'
Then print 'hash' as 'hex'
`;


const body = `{"query":"${msg.sign.query}", "variables":${encoded_variables}}`

msg.sign.zenKeys = `
{
    "keyring":
    {
        "eddsa": "${msg.sign.eddsa}"
    }
}`;

msg.sign.zenData = `
{
    "gql": "${Buffer.from(body, 'utf8').toString('base64')}",
}`;

let res = await zenroom.zencode_exec(sign_script, { data: msg.sign.zenData, keys: msg.sign.zenKeys})
            .then((result) => {
                console.log("Msg : ", result.result);
                const res = JSON.parse(result.result);
                console.log("Msg signature: ", res);
                return res;
            }).catch((error) => {
                console.error("Error in zenroom sign function: ", error);
                return error;

            });

msg.sign.signature = res;
return msg;

